//             <WeatherPreview city={city} weather={weather} />
//             <ForecastPlayback forecast={forecast} />
//           </>
//         )}
//       </div>
//     );
// };

return (
  <div className="relative h-screen">
    <WeatherPreview city={city} weather={weather} />
    <div className="absolute top-0 left-0 w-full p-4 bg-white bg-opacity-75 shadow-md z-10">
      <CitySelection onSearch={handleSearch} />
    </div>
    {weather && (
      <div className="absolute bottom-0 left-0 w-full p-4 bg-white bg-opacity-75 shadow-md z-10">
        <ForecastPlayback forecast={forecast} />
      </div>
    )}
  </div>
);
};

export default App;
