import React, { useState } from 'react';
import Search from './components/CitySelection';
import './index.css';
import WeatherPreview from './components/WeatherPreview';
import ForecastPlayback from './components/ForecastPlayback';

const App = () => {
  const [city, setCity] = useState('');
  const [weather, setWeather] = useState(null);
  const [forecast, setForecast] = useState(null);

  const handleSearch = (data) => {
    setCity(data.city);
    setWeather(data.weather);
    setForecast(data.forecast);
  };

  return (
    <div className="container mx-auto p-4">
      <h1 className="text-2xl font-bold mb-4">Weather Map</h1>
      <Search onSearch={handleSearch} />
      {weather && (
        <>
          <WeatherPreview city={city} weather={weather} />
          <ForecastPlayback forecast={forecast} />
        </>
      )}
    </div>
  );
};

export default App;
